.form-container {
  max-width: 600px;
  margin: 60px auto;
  background-color: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 0 10px rgba(0,0,0,0.15);
}

.form-title {
  text-align: center;
  margin-bottom: 20px;
  color: #1f2937;
}

.form-box label {
  display: block;
  margin-top: 15px;
  margin-bottom: 5px;
  font-weight: bold;
}

.form-box input,
.form-box textarea {
  width: 100%;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 6px;
  box-sizing: border-box;
  font-size: 1rem;
}

.form-box input:focus,
.form-box textarea:focus {
  border-color: #4f46e5;
  outline: none;
}

.btn-salvar {
  display: block;
  margin: 25px auto 0;
  background-color: #22c55e;
  color: white;
  font-weight: bold;
  border: none;
  padding: 10px 20px;
  border-radius: 6px;
  cursor: pointer;
  transition: background-color 0.3s;
}

.btn-salvar:hover {
  background-color: #16a34a;
}
